configuration.source:
def ant = new AntBuilder()
def target= "${project.basedir}/target"
def dir = "${project.basedir}/target/jeuclid_imagetoolkit-2_9_7"
ant.mkdir(dir: dir)
ant.copy(todir: dir) {
  fileset(dir: "${project.basedir}/target") {
    include(name: "jeuclid_imagetoolkit.jar")
  }
  fileset(dir: "${user.home}/.m2/repository/commons-logging/commons-logging/1.1/") {
    include(name: "commons-logging-1.1.jar")
  }
  fileset(dir: "${project.basedir}/../jeuclid-core/target/") {
    include(name: "jeuclid-core-2.9.8-SNAPSHOT.jar")
  }
  fileset(dir: "${project.basedir}/src/main/xxe/") {
    include(name: "**/*")
  }
}
ant.zip(destfile: "$target/jeuclid_imagetoolkit.zip", basedir: "$target", includes: "jeuclid_imagetoolkit-2_9_7/**" )
